entity word_ack_gen is
  port (
    adr : in std_logic_vector( 31 downto 0);
    word_bus_en : in std_logic ;
    ack_thru_in_aic0     : in std_logic ;
    ack_thru_in_aic1     : in std_logic ;
    ack_thru_in_emac     : in std_logic ;
