        -- Address decoding closer to CPU checks those bits.
        if addr(27 downto 18) = "1011110011" then
            if addr(17 downto 11) = "0100000" then
                if addr(10) = '0' then
                    if addr(9) = '0' then
                        if addr(8) = '0' then
                            if addr(7) = '0' then
                                if addr(6) = '0' then
