                                    return DEV_GPIO;
                                else
                                    -- ABCD0040-ABCD0047
                                    return DEV_FLASH;
                                end if;
                            else
                                -- ABCD00C0-ABCD00FF
                                return DEV_CACHE_CTRL;
                            end if;
                        else
                            -- ABCD0100-ABCD010F
                            return DEV_UART0;
                        end if;
                    else
                        -- ABCD0200-ABCD023F
                        return DEV_AIC0;
                    end if;
                else
                    -- ABCD0500-ABCD053F
                    return DEV_AIC1;
                end if;
            elsif addr(17) = '1' then
                -- ABCE0000-ABCE1FFF
                return DEV_EMAC;
            end if;
        end if;
        return NONE;
    end;
